    private void handleAcceptActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_handleAcceptActionPerformed
        handleAccept();
        
    }//GEN-LAST:event_handleAcceptActionPerformed

    private void handleAccept(){
        try{
            int optionClient = Integer.parseInt(option.getText());
            switch(optionClient){
                case 1 -> {
                    this.dispose();
                    new AddVehicule().setVisible(true);
                }
                case 2 -> {
                    this.dispose();
                    new AddClient().setVisible(true);
                }
                case 3 -> {
                    this.dispose();
                    new RegisterRent().setVisible(true);
                }
                case 4 -> {
                    this.dispose();
                    new FindVehicules().setVisible(true);
                }
                case 5 -> {
                    this.dispose();
                    new FindClients().setVisible(true);
                }
                case 6 -> {
                    this.dispose();
                    new FindRents().setVisible(true);
                }
                case 7 -> {
                    this.dispose();
                    new FindRent().setVisible(true);
                }
                case 8 -> System.exit(0);
                default -> throw new IllegalArgumentException("Opcion "+ option + " No valida");
            }
                
            
        }catch(NumberFormatException | NullPointerException ex){
            JOptionPane.showMessageDialog(null, "Opcion no valida");
        }
        catch(IllegalArgumentException ex){
            JOptionPane.showMessageDialog(null, ex.getMessage());
        }
    }
    private void send(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_send
        if(evt.getKeyCode() == 10)
            handleAccept();
        
    }//GEN-LAST:event_send

